mapper.select:
SELECT p.*
FROM plt p
         JOIN item_plt ip ON ip.plt_id = p.id
         JOIN item i ON i.id = ip.item_id
WHERE ip.item_id = #{itemId}
  AND i.user_id = #{userId}
  AND p.user_id = #{userId}

mapper.insert:
INSERT INTO plt (
    id, user_id, plt_name
)
VALUES

    (
        #{plt.id}, #{plt.userId}, #{plt.pltName}
    )

mapper.select:
SELECT COUNT(1)
FROM plt
WHERE user_id = #{userId}
  AND BINARY plt_name = #{name}
